const daycareReducer = (state = { daycares: []/*, loading: false */}, action) => {
    switch (action.type) {
      case "FETCHING_DAYCARES":
        // return {
        //   ...state,
        //   daycares: [...state.daycares],
        //   loading: true,
        // };
        return {daycares: action.daycares}
      case "ADD_DAYCARES":
        return {
          ...state,
          daycares: action.daycares,
          /*loading: false,*/
        };
      case 'ADD_TODDLER':
        let daycares = state.daycares.map(daycare => {
            if (daycare.id === action.daycares.id) {
              return action.daycares
            }else{
              return daycare
            }
        })
        return {...state, daycares: daycares }
      default:
        return state;

    }
  };

  export default daycareReducer;


