import React, { Component } from 'react'
import { connect } from 'react-redux'
import { deleteToddlers } from '../../actions/toddlerActions'
// import {Route, Link} from 'react-router-dom'


class ToddlerFilter extends Component {
    filter = () => {
        this.props.handleFilter()
    }


    render() {
        // let toddlerFilter = this.props.toddlerFilter
        const handleDelete = (toddler) => {
            this.props.deleteToddlers(toddler.id)
        }
        return (
            <div>
                {/* eslint-disable-next-line array-callback-return */}
                {this.filter.map( toddler => {
                    <div key={toddler.id} id="Toddler-filter">
                        <table class="table">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    {/* <th>Birthday</th>
                                    <th>Emergency Contact</th>
                                    <th>Phone</th>
                                    <th>Allergy</th> */}
                                </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>{toddler.name}</td>
                                        {/* <td>{toddler.birthday}</td>
                                        <td>{toddler.contact}</td>
                                        <td>{toddler.phone}</td>
                                        <td>{toddler.allergy}</td> */}
                                    </tr>
                                </tbody>
                        </table>
                        <button onClick={() => handleDelete(toddler)}>Delete</button>
                    </div>
                })}
            </div>
        )
    }
}

export default connect(null, { deleteToddlers })(ToddlerFilter)
