    switch (action.type) {
      case "FETCHING_DAYCARES":
        // return {
        //   ...state,
        //   daycares: [...state.daycares],
        //   loading: true,
        // };
        return {daycares: action.daycares}
      case "ADD_DAYCARES":
        return {
          ...state,
          daycares: action.daycares,
          /*loading: false,*/
        };
      default:
        return state;
        case 'ADD_EXERCISE':
         let workouts = state.workouts.map(workout => {
            if (workout.id === action.payload.id) {
               return action.payload
            }else{
               return workout
            }
         })
    }
  };

  export default daycareReducer;


