import React, { Component } from 'react'
import { connect } from 'react-redux'
import { submitToddlers } from '../../actions/toddlerActions'

class SunnyDayForm extends Component {
    constructor() {
        super()
        this.state = {
            name: '',
            birthday: '',
            contact: '',
            phone: '',
            allergy: ''
        }
    }

    handleOnChange = (event) => {
        console.log(event.target.value, "Sunny Day Form: handle on change")
        this.setState({[event.target.name]: event.target.value})
    }

    handleOnSubmit = (event) => {
        event.preventDefault()
        // debugger
        this.props.submitToddlers(this.state, this.props.daycare.id)
        this.setState({
            name: '',
            birthday: '',
            contact: '',
            phone: '',
            allergy: ''
        })
    }

    render() {
        return (
            <div>

            </div>
        )
    }
}

export default connect(null, { submitToddlers })(SunnyDayForm)
