import React, { Component } from 'react'

export default class ToddlerFilter extends Component {
    constructor(){
        super()
        this.state = {
            value: ''
        }
    }

    handleOnChange = (event) => {
        this.setState({value: event.target.value})
    }

    render() {
        console.log(this.props.getToddlers, "Toddler Filter")
        return (
            <div>
                <input type="text" value={this.state.value} onChange={this.handleOnChange} placeholder="filter Child..."  /><br />
            </div>
        )
    }
}
