const daycareReducer = (state = { daycares: [], loading: false}, action) => {
    switch (action.type) {

        case 'LOADING_DAYCARES':
            return {
            ...state, 
            daycares: [...state.daycares], 
            loading: true
        }
        case 'ADD_DAYCARES':
            return {
            ...state,
            daycares: action.daycares,
            loading: false
        }       

        case 'ADD_TODDLER':
            let daycares = state.daycares.map(daycare => {
               if (daycare.id === action.payload.id) {
                  return action.payload
               }else{
                  return daycare
               }
            })
            return {...state, daycares: daycares }

        default:
            return state

    }

}

export default daycareReducer
