import React, { Component } from 'react'
import ToddlerFilterContainer from '../../containers/ToddlerFilterContainer'
import ToddlerTableRow from '../toddlercomp/ToddlerTableRow'

// Daycare component mapping through the props recieved from daycare container and passing props down to toddlerlist and toddler conatainer

class Daycare extends Component {

    state = {
        initialArray: this.props.getDaycare.toddlers,
        newArray: []
    }

    toddlerSort = () => {

        this.setState({...this.state, newArray: this.state.initialArray.sort((t, u) => {
            if (t.name < u.name) {return -1;}
            if (t.name > u.name) {return 1;}
            return 0;

        })})
        console.log(this.state.initialArray)

    }

    render() { 

        return (
            <div id="table-list">
                <br />
                    <button onClick={this.toddlerSort}>Sort</button>
                    {/* eslint-disable-next-line array-callback-return*/}
                    {this.props.getDaycare.toddlers.map( toddler => { //Passing props containing an array of toddlers and  their info
                        return <ToddlerTableRow key={toddler.id} toddlerDetail={toddler} />
                    })}
                    {this.state.newArray}
                     {/* passing props of the whole daycare object */}
                <ToddlerFilterContainer getToddlers={this.props.getDaycare}/> 
            </div>
        )
    }
}

export default Daycare
